mapper.select:

where d.del_flag = '0'

    AND d.id = #{dept.deptId}


    AND parent_id = #{dept.parentId}


    AND dept_name like concat('%', #{dept.deptName}, '%')


    AND status = #{dept.status}

order by d.parent_id, d.order_num

select d.id from sys_dept d left join sys_role_dept rd on d.id = rd.dept_id where rd.role_id = #{roleId}
and d.id not in (select d.parent_id from sys_dept d inner join sys_role_dept rd on d.id = rd.dept_id and rd.role_id = #{roleId})
order by d.parent_id, d.order_num

mapper.update:
update sys_dept set ancestors =

    when #{item.deptId} then #{item.ancestors}

where id in

    #{item.deptId}
